import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import { getCookie } from '../Login/Login';
import './profile.css';

const Profile = (props) => {
  const [email, setEmail] = useState('');
  const [contact, setContact] = useState('');
  const [age, setAge] = useState('');
  const [inst_name, setInst_name] = useState('');

  useEffect(() => {
    // Fetch the user details from cookies and set the state
    setEmail(getCookie("user_email"));
    setContact(getCookie("user_contact"));
    setAge(getCookie("user_age"));
    setInst_name(getCookie("user_inst_name"));
  }, []); // Empty dependency array to run the effect only once when the component mounts

  return (
    <div className="body">
      <div className="profile">
        <h1>User Profile</h1>
        <form action="POST">
        <div>
        <img class="profile_img" src="https://source.unsplash.com/600x300/?student" alt="student dp"/>
        </div>
          <label htmlFor="name">Full name:</label>
          <input type="text" id="name" required value={getCookie("user_name")} placeholder="Full Name" readOnly />
          <label htmlFor="email">Email address:</label>
          <input type="text" id="email" required placeholder="Email Address" value={email} onChange={(e) => setEmail(e.target.value)} /><br />
          <label htmlFor="contact">Contact Number:</label>
          <input type="text" id="contact" maxLength="10" required placeholder="Mobile Number" value={contact} onChange={(e) => setContact(e.target.value)} />
          <label htmlFor="age">Age:</label>
          <input type="text" id="age" maxLength="2" required placeholder="Age in number" value={age} onChange={(e) => setAge(e.target.value)} />
          <label htmlFor="Institute">Name of the Institute/School</label>
          <input type="text" id="Institute" required placeholder="Name of Institute.." value={inst_name} onChange={(e) => setInst_name(e.target.value)} />
          <NavLink to="/Homepg" className="home-button">Back Home</NavLink>
        </form>
      </div>
    </div>
  );
};

export default Profile;
